import React from 'react';

const Dashboard = ({ theme }) => {
  // Cambia el theme del dashboard de MongoDB Charts según el modo
  const chartsTheme = theme === 'dark' ? 'dark' : 'light';
  return (
    <div style={{ width: '100%', minHeight: '80vh', padding: 0 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Dashboard de Reportes</h2>
      </div>
      <div className="dashboard-charts-row">
        <iframe
          className="dashboard-chart"
          style={{ background: theme === 'dark' ? '#232a36' : '#F1F5F4', border: 'none', borderRadius: 2, boxShadow: '0 2px 10px 0 rgba(70, 76, 79, .2)' }}
          src={`https://charts.mongodb.com/charts-project-0-gvtlivt/embed/charts?id=664b2e2d-2e7e-4b7e-8b2e-1e2e2e2e2e2e&theme=${chartsTheme}`}
          title="Numero de Informes"
          allowFullScreen
        />
        <iframe
          className="dashboard-chart"
          style={{ background: theme === 'dark' ? '#232a36' : '#F1F5F4', border: 'none', borderRadius: 2, boxShadow: '0 2px 10px 0 rgba(70, 76, 79, .2)' }}
          src={`https://charts.mongodb.com/charts-project-0-gvtlivt/embed/charts?id=664b2e2d-2e7e-4b7e-8b2e-1e2e2e2e2e2f&theme=${chartsTheme}`}
          title="Numero de Informes por Area"
          allowFullScreen
        />
        <iframe
          className="dashboard-chart"
          style={{ background: theme === 'dark' ? '#232a36' : '#F1F5F4', border: 'none', borderRadius: 2, boxShadow: '0 2px 10px 0 rgba(70, 76, 79, .2)' }}
          src={`https://charts.mongodb.com/charts-project-0-gvtlivt/embed/charts?id=664b2e2d-2e7e-4b7e-8b2e-1e2e2e2e2e2g&theme=${chartsTheme}`}
          title="Numero de Informes Dia/Noche"
          allowFullScreen
        />
      </div>
      <button
        style={{ marginTop: 24, padding: '10px 24px', fontSize: 16, borderRadius: 4, border: 'none', background: '#4F8A8B', color: '#fff', cursor: 'pointer' }}
        onClick={() => window.location.href = '/'}
      >
        Volver
      </button>
    </div>
  );
};

export default Dashboard;
