import React from 'react';

const Dashboard = ({ theme }) => {
  // Cambia el theme del dashboard de MongoDB Charts según el modo
  const chartsTheme = theme === 'dark' ? 'dark' : 'light';
  const iframeBg = theme === 'dark' ? '#21313C' : '#FFFFFF';
  return (
    <div style={{ width: '100%', minHeight: '80vh', padding: 0 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Dashboard de Reportes</h2>
      </div>
      {/* Primer iframe responsivo con chartsTheme y fondo dinámico */}
      <div style={{ position: 'relative', width: '100%', paddingBottom: '75%', marginBottom: 32 }}>
        <iframe
          style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', background: iframeBg, border: 'none', borderRadius: 2, boxShadow: '0 2px 10px 0 rgba(70, 76, 79, .2)' }}
          src={`https://charts.mongodb.com/charts-project-0-gvtlivt/embed/charts?id=c10172c4-989d-457e-8dad-9cbb8f9ee3fe&maxDataAge=14400&theme=${chartsTheme}&autoRefresh=true`}
          title="Reporte 1"
          allowFullScreen
        />
      </div>
      {/* Aquí irán los otros dos iframes, también usando chartsTheme y fondo dinámico */}
      <button
        style={{ marginTop: 24, padding: '10px 24px', fontSize: 16, borderRadius: 4, border: 'none', background: '#4F8A8B', color: '#fff', cursor: 'pointer' }}
        onClick={() => window.location.href = '/'}
      >
        Volver
      </button>
    </div>
  );
};

export default Dashboard;
